import React, { useState, useEffect } from "react";
import { Card, ListItem, Button, Icon } from "react-native-elements";

//import ImagePicker from 'react-native-image-picker'

import {
  StyleSheet,
  Text,
  TextInput,
  View,
  SafeAreaView,
  TouchableOpacity,
  Image,
  Linking,
} from "react-native";

//import { set } from "react-native-reanimated";
import { FontAwesome } from "@expo/vector-icons";

import api from "../api/api";

function NewPost({ navigation }) {
  const { IdAluno } = navigation.state.params;

  const [nomeArquivo, setNomeArquivo] = useState("");
  const [categoria, setCategoria] = useState("");

  const [photo, setPhoto] = useState([]);

    
  function handleChoosePhoto(){
    
      const option = {
        noData: true
      };
  };

  async function EnviarArquivo(base64EncodedImage) {
    try {
      await api.post("/arquivo", {
        headers: {
          Authorization: "",
          "Content-type": "application/json",
        },

        Data: base64EncodedImage,
        NomeArquivo: nomeArquivo,
        Categoria: categoria,
        IdAluno_Arquivos: IdAluno,
      });
    } catch (error) {
      console.error(error);
    }
  }

  return (
    <View style={styles.newPostPage}>
      <Card containerStyle={styles.cardBox}>
        <View style={styles.newPostHeader}>
          <Text style={styles.titles}>Nova Postagem</Text>
        </View>

        <View style={styles.newPostBody}>
          <View style={styles.sendFileButton}>
            <TouchableOpacity
              onPress={() => {
              }}
            >
              <FontAwesome
                name="download"
                size={90}
                style={styles.btnNewItemDesign}
                color="#FFF"
              >
                <Text style={styles.titles}>Selecione o Arquivo</Text>
              </FontAwesome>
            </TouchableOpacity>
          </View>
        </View>
        <View>
    
        </View>
        <View style={styles.newPostFooter}>
          <Button
            title="ENVIAR"
            size="sm"
            type="outline"
            color="(255, 255, 255, 0)"
          />
        </View>
      </Card>
    </View>
  );
}
const styles = StyleSheet.create({
  newPostPage: {
    flex: 1,
    justifyContent: "flex-start",
    backgroundColor: "#000",
  },
  cardBox: {
    borderRadius: 4,
    borderColor: "#00000000",
    backgroundColor: "rgb(18,18,18)",
    padding: 0,
    width: 600,
    height: 250,
    width: "100%",
    alignItems: "center",
  },
  newPostHeader: {
    flex: 1,
    alignItems: "center",
    margin: 5,
  },
  sendFileButton: {
    backgroundColor: "#000",
  },
  newPostBody: {
    flex: 2,
    padding: 5,
    textAlign: "center",
  },
  newPostFooter: {
    borderWidth: 2,
    //borderColor: "#03DAC6",
    color: "#00000000",
    justifyContent: "center",
    marginHorizontal: 80,
    marginVertical: 20,
    paddingVertical: 10,
    paddingHorizontal: 10,
  },
  titles: {
    fontSize: 30,
    color: "#fff",
  },
});

export default NewPost;
